package com.goldenasia.lottery.bean;import java.util.ArrayList;import java.util.List;public class Category {	private HistoryLottery lotteryTitle=new HistoryLottery();		private List<HistoryLottery> mCategoryItem = new ArrayList<HistoryLottery>();		public Category() {	}	public Category(HistoryLottery lotteryTitle) {		this.lotteryTitle=lotteryTitle;	}	public HistoryLottery getLotteryTitle() {		return lotteryTitle;	}	public void setLotteryTitle(HistoryLottery lotteryTitle) {		this.lotteryTitle = lotteryTitle;	}	public List<HistoryLottery> getmCategoryItem() {		return mCategoryItem;	}	public void setmCategoryItem(List<HistoryLottery> mCategoryItem) {		this.mCategoryItem = mCategoryItem;	}}